    })
    .then(function (data) {

      axios
        .request(options)
        .then(function (response) {
          setQrcodeSrc(response.data);
        })
        .catch(function (error) {
          console.error(error);
        });
    })
    .catch(function (error) {
      console.error(error);
    });

  }, []);
  // Make a GET request

  return (
    <>
      <h1>Scan the barcode from Google Authenticator</h1>;
      <img src={qrcodeSrc} />
    </>
