import * as React from "react";
import { Modal } from "react-bootstrap";
import Comment from "./Comment";
import PostForm from "./PostForm";

export default function Comments({
  parentPost,
  posts,
  user,
  dispatchUser,
  addPost,
  editPost,
  removePost,
  commentId,
  commentShow,
  showComment,
}) {
  const [parentId, setParentId] = React.useState(parentPost.postId);
  const [replyTo, setReplyTo] = React.useState(null);
  const [commentsHTML, setcommentsHTML] = React.useState(
    parentPost.children
      .filter((postIdx) => !posts[postIdx].isDeleted)
      .map((postIdx) => (
        <Comment
          key={postIdx}
          parentPost={parentPost}
          posts={posts}
          commentId={postIdx}
          user={user}
          dispatchUser={dispatchUser}
          addPost={addPost}
          removePost={removePost}
          editPost={editPost}
          replyHandler={replyHandler}
          expandComment={expandComment}
        ></Comment>
      ))
  );
  let isComment = true;

  const expandComment = (id) => {};

  const replyHandler = (parentId, replyTo) => {
    setParentId(parentId);
    setReplyTo(replyTo);
  };

  return (
    <Modal show={commentShow} onHide={showComment} centered>
      <Modal.Header closeButton className="text-center">
        Comments
      </Modal.Header>
      <Modal.Body></Modal.Body>
      <Modal.Footer>
        <PostForm
          user={user}
          dispatchUser={dispatchUser}
          addPost={addPost}
          postId={commentId}
          isComment={isComment}
          parentPostId={parentId}
          replyTo={replyTo}
        />
      </Modal.Footer>
    </Modal>
  );
}
