import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import * as React from "react";

export default function SignUpForm() {
  const [isPasswordInvalid, setIsPasswordInvalid] = React.useState(false);
  const [isPasswordConfirmed, setIsPasswordConfirmed] = React.useState(false);
  const [isPasswordVisible, setIsPasswordVisble] = React.useState(false);

  let timeoutId;

  const validatePassword = (event) => {
    // Only validate a second after the user stops typing
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      /* A regex pattern that matches invalid passwords
         - Anything with less than eight characters OR
         - Anything with no numbers OR
         - Anything with no uppercase letter OR
         - Anything with no lowercase letter OR
         - Anything with no special characters
      */
      const regex = "^(.{0,7}|[^0-9]*|[^A-Z]*|[^a-z]*|[a-zA-Z0-9]*)$";
      console.log(event.target.value.match(regex));

      // If there are any matches, the password is invalid
      setIsPasswordInvalid(event.target.value.match(regex) ? true : false);
    }, 1000);
  };

  const togglePasswordVisability = (event) => {
    setIsPasswordVisble(!isPasswordVisible);
  };

  return (
    <Form className="mt-5 mb-5">
      <header className="mb-5">
        <h1 className="text-center">Sign Up</h1>
      </header>
      <Form.Group className="mb-4" controlId="formSignupFirstName">
        <Form.Control
          type="text"
          required
          placeholder="First name"
          aria-label="First name"
        ></Form.Control>
      </Form.Group>
      <Form.Group className="mb-4" controlId="formSignupLastName">
        <Form.Control
          type="text"
          required
          placeholder="Last name"
          aria-label="Last name"
        ></Form.Control>
      </Form.Group>
      <Form.Group className="mb-5" controlId="formSignupEmail">
        <Form.Control
          type="email"
          required
          placeholder="Email"
          aria-label="Email"
        />
      </Form.Group>

      <Form.Group className="mb-4" controlId="formSignupPassword">
        <Form.Control
          type={isPasswordVisible ? "text" : "password"}
          required
          placeholder="Password"
          aria-label="Password"
          onChange={validatePassword}
          isInvalid={!!isPasswordInvalid}
          value="abcDEF1!"
        />
        <Form.Control.Feedback type="invalid">
          Use 8 or more character with a mix of lowercase and uppercase letters,
          numbers & symbols
        </Form.Control.Feedback>
      </Form.Group>

      <Form.Group className="mb-2" controlId="formSignupPasswordConfirm">
        <Form.Control
          type={isPasswordVisible ? "text" : "password"}
          required
          placeholder="Confirm"
          aria-label="Confirm"

        />
        <Form.Text className="text-muted">
          Use 8 or more character with a mix of lowercase and uppercase letters,
          numbers & symbols
        </Form.Text>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formSignupShowPassword">
        <Form.Check
          type="checkbox"
          label="Show password"
          onClick={togglePasswordVisability}
        />
      </Form.Group>
      <Button variant="primary" type="submit">
        Sign Up
      </Button>
    </Form>
  );
}
