import { Container, Card } from "react-bootstrap";
import { HandThumbsUp, ChatLeft } from "react-bootstrap-icons";
export default function PostContainer({ post }) {
  return (
    <Card>
      <Card.Header>{post.postBy}</Card.Header>
      <Card.Body>
        {post.text}
        <hr></hr>
        <img src={post.img} className="img-fluid"></img>
      </Card.Body>
      <Card.Footer>
        <Container className="d-flex">
          <div className="pl-2">
            <HandThumbsUp />
            <span>Like</span>
          </div>
          <div className="pl-2">
            <ChatLeft />
            <span>Comment</span>
          </div>
        </Container>
      </Card.Footer>
    </Card>
  );
}
