import * as React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Footer from "./UI/Footer";
import NavigationBar from "./UI/NavigationBar";
import SignUpForm from "./UI/SignUpForm";
import Container from "react-bootstrap/esm/Container";
import Hero from "./UI/Hero";
import { Outlet, Routes, Route } from "react-router-dom";

// Add a user to localStorage.users
function addUserToLS(user) {
  let key = JSON.stringify(user.email);
  // Delete the property user.email in user so that no duplicate information is stored
  let value = JSON.stringify(user);

  // If a user is added for the first time
  if (localStorage.getItem("users") === null) {
    localStorage.setItem("users", `{${key}:${value}}`);
  } else {
    let users = localStorage.getItem("users");
    localStorage.setItem("users", `${users.slice(0, -1)}, ${key}:${value}}`);
  }
}

// Add a user to sessionStorage.user
function addUserToSS(user) {
  sessionStorage.setItem("user", JSON.stringify(user));
}

function removeUserFromSS() {
  sessionStorage.removeItem("user");
}

function editUser(state, action) {

  // Update users
  state.users[action.payload.email].firstName = action.payload.firstName;
  state.users[action.payload.email].lastName = action.payload.lastName;

  // Update localStorage
  localStorage.setItem("users", JSON.stringify(state.users));

  return state;
}

const userReducer = (state, action) => {
  switch (action.type) {
    case "SIGNUP_USER":
      addUserToLS(action.payload);
      addUserToSS(action.payload);
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
        users: localStorage.getItem("users"),
      };
    case "SIGNIN_USER":
      addUserToSS(action.payload);
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
      };
    case "SIGNOUT_USER":
      removeUserFromSS();
      return {
        ...state,
        data: null,
        isLoggedIn: false,
      };
    case "EDIT_USER":
      addUserToSS(action.payload)
      editUser(state, action);
      return {
        ...state,
        data: action.payload,
      };

    default:
      throw new Error();
  }
};

function App() {
  const [user, dispatchUser] = React.useReducer(userReducer, {
    data: JSON.parse(sessionStorage.getItem("user")),
    isLoggedIn: sessionStorage.getItem("user") !== null,
    // a mock database holding informaiton on users
    users: JSON.parse(localStorage.getItem("users")) ?? [],
  });

  return (
    <React.Fragment>
      <NavigationBar isLoggedIn={user.isLoggedIn} dispatchUser={dispatchUser} />
      <Outlet context={[user, dispatchUser]} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
