import * as React from "react";
import {
  Row,
  Col,
  Card,
  Form,
  Button,
  Container,
  ProgressBar,
  OverlayTrigger,
  Tooltip,
} from "react-bootstrap";
import { Image } from "react-bootstrap-icons";
import Post from "./Post";

const postUrl = "https://api.cloudinary.com/v1_1/duc4zmhl7/image/upload";
const resUrl = "https://res.cloudinary.com/duc4zmhl7/image/upload";

export default function PostForm({ user, dispatchUser, addPost, postId }) {
  const [isPostable, setIsPostable] = React.useState(false);
  const [numCharsTyped, setNumCharsTyped] = React.useState(0);
  const [progressBarVariant, setProgressBarVariant] = React.useState("success");
  const wordLimit = 250;

  React.useEffect(() => {
    // Disable/enable the post button
    clearTimeout(validateTextareaTimeoutId);
    validateTextareaTimeoutId = setTimeout(() => {
      setIsPostable(validateTextarea(numCharsTyped));
    }, 500);

    if (Math.floor((numCharsTyped / wordLimit) * 100) <= 70) {
      setProgressBarVariant("success");
    } else if (numCharsTyped <= wordLimit) {
      setProgressBarVariant("warning");
    } else {
      setProgressBarVariant("danger");
    }
  }, [numCharsTyped]);

  let validateTextareaTimeoutId;

  const validateTextarea = (num) => num > 0 && num <= wordLimit;

  const textareaChangeHandler = (event) => {
    setNumCharsTyped(event.target.value.length);
  };

  const imageUploadHandler = (event) => {
    let img = document.querySelector("#myImg");
    img.src = URL.createObjectURL(event.target.files[0]);
  };

  const postHandler = async (e) => {
    e.preventDefault();

    let imagesToUpload = document.querySelector("#imageUpload").files;
    if (imagesToUpload.length != 0) {
      const data = new FormData();
      data.append("file", imagesToUpload[0]);
      data.append("upload_preset", "zqlcfaas");

      fetch(postUrl, {
        method: "POST",
        body: data,
      })
        .then((response) => response.json())
        .then((data) => {
          addPost(
            {
            email: user.data.email,
            text: e.target[0].value,
            img: `${resUrl}/v${data.version}/${data.public_id}.${data.format}`,
            datePosted: new Date(),
            postId: postId,
          });
          e.target.reset();
          e.target.querySelector("img").src = "#";
          setIsPostable(false);
        })
        .catch((error) => console.log(error));
    } else {
      // If no image was attached
      addPost({
        email: user.data.email,
        text: e.target[0].value,
        img: null,
        datePosted: new Date(),
        postId: postId,
      });
      e.target.reset();
      setIsPostable(false);
    }
  };

  return (
    <Card>
      <img src="#"></img>
      <Row className="gx-0">
        <Col xs={2}>
          <Card.Img src={user.data.avatarsrc}></Card.Img>
        </Col>
        <Col>
          <Form onSubmit={postHandler}>
            <Form.Control
              as="textarea"
              rows={3}
              onChange={textareaChangeHandler}
            ></Form.Control>
            <img id="myImg" src="#" className="img-fluid"></img>

            <ProgressBar
              className="my-3"
              variant={progressBarVariant}
              now={Math.floor((numCharsTyped / wordLimit) * 100)}
              label={
                progressBarVariant !== "success" &&
                `${wordLimit - numCharsTyped} letters left`
              }
            ></ProgressBar>

            <Container className="d-flex justify-content-between mb-3">
              <Form.Group controlId="imageUpload">
                <Form.Label>
                  <Image></Image>
                </Form.Label>
                <Form.Control
                  type="file"
                  className="d-none"
                  onChange={imageUploadHandler}
                ></Form.Control>
              </Form.Group>

              <Button type="submit" disabled={!isPostable}>
                Post
              </Button>
            </Container>
          </Form>
        </Col>
      </Row>
    </Card>
  );
}
