import * as React from "react";
import { Container, Form, Button } from "react-bootstrap";
import { useNavigate, useOutletContext } from "react-router-dom";
import Loading from "./Loading";
import MFA from "./MFA";

export default function SignInForm() {
  const [, dispatchUser, users] = useOutletContext();
  const [isEmailValid, setIsEmailValid] = React.useState(true);

  const [isLoading, setIsLoading] = React.useState(false);
  const [isMFAVisible, setIsMFAVisible] = React.useState(false);
  const [user, setUser] = React.useState(null);
  const navigate = useNavigate();


  const handleSubmit = (event) => {
    event.preventDefault();
    let email = event.target[0].value;
    let password = event.target[1].value;
    if (email in users) {
      if (users[email].password === password) {
        setUser({
          ...user,
          email: email,
          password: password,
          secret: users[email].secret,
        });
        setIsPasswordCorrect(true);
        setIsEmailValid(true);
        setIsMFAVisible(true);
      } else {
        // Password doesn't match
        setIsEmailValid(true);
        setIsPasswordCorrect(false);
      }
    } else {
      // Email doesn't exist
      setIsEmailValid(false);
      setIsPasswordCorrect(true);
    }
  };

  const signInAuthenticatedUser = () => {
    dispatchUser({ type: "SIGNIN_USER", payload: users[user.email] });
    setIsMFAVisible(false);
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      navigate("/profile");
    }, timeDelayed);
  };

  return (
    <Container>
      <MFA
        show={isMFAVisible}
        setShow={setIsMFAVisible}
        hideQRcode={true}
        onSuccess={signInAuthenticatedUser}
        user={user}
      />
      <Loading show={isLoading}>Logging in</Loading>
      <Form className="my-5" onSubmit={handleSubmit}>
        <header className="mb-5">
          <h1 className="text-center">Sign in</h1>
        </header>
        <Form.Group className="mb-5" controlId="formSignInEmail">
          <Form.Label visuallyHidden>Email</Form.Label>
          <Form.Control
            type="email"
            required
            placeholder="Email"
            isInvalid={!isEmailValid}
          />
          <Form.Control.Feedback type="invalid">
            The email doesn't exist
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group className="mb-5" controlId="formSignInPassword">
          <Form.Label visuallyHidden>Password</Form.Label>
          <Form.Control
            type="password"
            required
            placeholder="Password"
            isInvalid={!isPasswordCorrect}
          />
          <Form.Control.Feedback type="invalid">
            The password doesn't match
          </Form.Control.Feedback>
        </Form.Group>
        <Button variant="primary" type="submit">
          Sign In
        </Button>
      </Form>
    </Container>
  );
}
