import * as React from "react";
import PostForm from "./PostForm";
import { Container } from "react-bootstrap";
import { useOutletContext } from "react-router-dom";
import Post from "./Post";

const numPosts = 5;

export default function Posts() {
  const [user, dispatchUser] = useOutletContext();
  // First read posts from localStorage if any. Otherwise, initiate posts with an empty array
  const [posts, setPosts] = React.useState(
    JSON.parse(localStorage.getItem("posts")) ?? []
  );

  const addPost = (item) => {
    setPosts([...posts, item]);
    dispatchUser({
      type: "ADD_POST",
      payload: posts.length,
    });
  };

  const removePost = (id) => {
    posts[id].isDeleted = true;
    setPosts([...posts]);
    dispatchUser({
      type: "DELETE_POST",
      payload: id,
    });
  };

  const editPost = (id, text) => {
    posts[id].text = text;
    setPosts([...posts]);
  }


  React.useEffect(() => {
    // TODO: write a more efficient code
    localStorage.setItem("posts", JSON.stringify(posts));
  }, [posts]);

  return (
    <Container className="my-5">
      <PostForm
        user={user}
        dispatchUser={dispatchUser}
        addPost={addPost}
        postId={posts.length}
      ></PostForm>
      {posts
        .filter((post) => !post.isDeleted)
        .slice(-numPosts)
        .reverse()
        .map((post) => (
          <Post
            key={post.postId}
            user={user}
            dispatchUser={dispatchUser}
            post={post}
            postId={postId}
            addPost={addPost}
            removePost={removePost}
            editPost={editPost}
          ></Post>
        ))}
    </Container>
  );
}
