import * as React from "react";
import {
  Toast,
  ToastContainer,
  Form,
  Button,
  Modal,
  Accordion,
} from "react-bootstrap";
import {
  ArrowReturnRight,
  PencilFill,
  TrashFill,
  ChatLeft,
} from "react-bootstrap-icons";

export default function Comment({
  parentPost,
  posts,
  commentId,
  user,
  dispatchUser,
  addPost,
  removePost,
  editPost,
  replyHandler,
  expandComment,
}) {
  const [editHidden, setEditHidden] = React.useState(true);
  const [deleteModalHidden, setDeleteModalHidden] = React.useState(true);

  const editHandler = () => setEditHidden(false);

  const confirmDeleteHandler = () => setDeleteModalHidden(false);
  const closeDeleteHandler = () => setDeleteModalHidden(true);

  const replyClickHandler = () => {
    replyHandler(commentId, posts[commentId].postBy);
  };

  const deleteHandler = () => {
    removePost(commentId);
    setDeleteModalHidden(true);
  };

  const editSubmitHandler = (e) => {
    e.preventDefault();
    editPost(commentId, e.target[0].value);
    setEditHidden(true);
  };

  const showMoreCommentsHandler = () => {
    expandComment(commentId);
  };

  return (
    <>
      <ToastContainer className="position-relative ml-2">
        <Modal show={!deleteModalHidden} onHide={closeDeleteHandler}>
          <Modal.Header closeButton>
            <Modal.Title>Delete Account</Modal.Title>
          </Modal.Header>
          <Modal.Body>Confirm to delete your account.</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={closeDeleteHandler}>
              Close
            </Button>
            <Button variant="danger" onClick={deleteHandler}>
              Delete
            </Button>
          </Modal.Footer>
        </Modal>
        <Toast>
          <Toast.Header closeButton={false} className="d-flex">
            <img
              src={user.data.avatarsrc}
              className="rounded me-2"
              width="30px"
              alt=""
            ></img>
            <strong className="me-auto">{user.data.email}</strong>
            <div>
              <PencilFill onClick={editHandler}></PencilFill>
              <TrashFill onClick={confirmDeleteHandler}></TrashFill>
            </div>
          </Toast.Header>
          <Toast.Body>
            {editHidden ? (
              posts[commentId].text
            ) : (
              <Form onSubmit={editSubmitHandler}>
                <Form.Group className="mb-3" controlId="formEditText">
                  <Form.Label visuallyHidden="true">Text</Form.Label>
                  <Form.Control
                    as="textarea"
                    required
                    defaultValue={posts[commentId].text}
                  ></Form.Control>
                </Form.Group>
                <Button variant="primary" type="submit">
                  Edit
                </Button>
              </Form>
            )}

            <hr></hr>
            <div className="d-flex justify-content-between">
              <a
                className="text-decoration-none"
                href="#"
                onClick={replyClickHandler}
              >
                <ArrowReturnRight></ArrowReturnRight> Reply
              </a>
              <a
                className="text-decoration-none"
                href="#"
                onClick={showMoreCommentsHandler}
              >
                <Accordion>
                  <Accordion.Item eventKey="0">
                    <Accordion.Header>
                      <ChatLeft />:{posts[commentId].children.length}
                    </Accordion.Header>
                  </Accordion.Item>
                </Accordion>
              </a>

              <small>mins ago</small>
            </div>
          </Toast.Body>
        </Toast>
      </ToastContainer>
    </>
  );
}
