import * as React from "react";
import Container from "react-bootstrap";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { useNavigate, useOutletContext } from "react-router-dom";
import Loading from "./Loading";

export default function SignInForm() {
  const [user, dispatchUser, users] = useOutletContext();
  const [isEmailValid, setIsEmailValid] = React.useState(true);
  const [isPasswordMatched, setIsPasswordMatched] = React.useState(true);
  const [isLoading, setIsLoading] = React.useState(false);
  const navigate = useNavigate();
  const timeDelayed = 2000;

  const handleSubmit = (event) => {
    event.preventDefault();
    let email = event.target[0].value;
    let password = event.target[1].value;
    if (email in users) {
      if (users[email].password === password) {
        setIsPasswordMatched(true);
        setIsEmailValid(true);
        dispatchUser({ type: "SIGNIN_USER", payload: users[email] });
        setIsLoading(true);
        setTimeout(() => {
          setIsLoading(false);
          navigate("/profile");
        }, timeDelayed);
      } else {
        // Password doesn't match
        setIsEmailValid(true);
        setIsPasswordMatched(false);
      }
    } else {
      // Email doesn't exist
      setIsEmailValid(false);
      setIsPasswordMatched(true);
    }
  };

  return (
    <Container>
      {isLoading && <Loading>Logging in</Loading>}
      <Form className="my-5" onSubmit={handleSubmit}>
        <header className="mb-5">
          <h1 className="text-center">Sign in</h1>
        </header>
        <Form.Group className="mb-5" controlId="formSignInEmail">
          <Form.Label visuallyHidden>Email</Form.Label>
          <Form.Control
            type="email"
            required
            placeholder="Email"
            isInvalid={!isEmailValid}
          />
          <Form.Control.Feedback type="invalid">
            The email doesn't exist
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group className="mb-5" controlId="formSignInPassword">
          <Form.Label visuallyHidden>Password</Form.Label>
          <Form.Control
            type="password"
            required
            placeholder="Password"
            isInvalid={!isPasswordMatched}
          />
          <Form.Control.Feedback type="invalid">
            The password doesn't match
          </Form.Control.Feedback>
        </Form.Group>
        <Button variant="primary" type="submit">
          Sign In
        </Button>
      </Form>
    </Container>
  );
}
