import * as React from "react";
import { Modal } from "react-bootstrap";
import PostForm from "./PostForm";

export default function Comment({
  post,
  posts,
  user,
  dispatchUser,
  addPost,
  commentId,
  commentShow,
  showComment,
}) {
  let isComment=true;

  return (
    <Modal show={commentShow} onHide={showComment} centered>
      <Modal.Header closeButton className="text-center">
        Comments
      </Modal.Header>
      <Modal.Body>
        {post.children.map((postIdx) => (
          <p key={postIdx}>{posts[postIdx].text}</p>
        ))}
      </Modal.Body>
      <Modal.Footer>
        <PostForm
          user={user}
          dispatchUser={dispatchUser}
          addPost={addPost}
          postId={commentId}
          isComment={true}
          parentPostId={post.postId}
        />
      </Modal.Footer>
    </Modal>
  );
}
