import * as React from "react";
import { Card, Container, Form } from "react-bootstrap";
import { useOutletContext } from "react-router-dom";
import { PencilFill, TrashFill } from "react-bootstrap-icons";
import EditProfile from "./EditProfile";

export default function Profile() {
  const [user, dispatchUser] = useOutletContext();
  const [editHidden, setEditHidden] = React.useState(true);

  const editShowHandler = (event) => {
    setEditHidden(!editHidden);
  };

  const profileJSX = (
    <Container>
    <Card>
      <Card.Header className="d-flex justify-content-between align-items-center">
        <h1>Profile</h1>
        <span>
          <a href="#" onClick={editShowHandler}>
            <PencilFill />
          </a>
          <a href="#">
            <TrashFill />
          </a>
        </span>
      </Card.Header>
      <Card.Img
        variant="top"
        src="https://avatars.dicebear.com/api/male/test.svg"
      />
      <Card.Body>
        <Card.Title hidden={!editHidden}>
          {user.data.firstName + " " + user.data.lastName}
        </Card.Title>
        <Form hidden={editHidden}>
          <Form.Group controlId="formEditFirstName">
            <Form.Label visuallyHidden="true">First name</Form.Label>
            <Form.Control
              type="text"
              required
              placedholder={user.data.firstName}
            ></Form.Control>
          </Form.Group>
        </Form>
        <Card.Subtitle className="mb-2 text-muted">
          {user.data.email}
        </Card.Subtitle>
        <hr />
        <Card.Text>Joined: {user.data.joinedDate}</Card.Text>
      </Card.Body>
    </Card>
    </Container>
  );

  return ;
}
