import * as React from "react";
import { Modal, Container, Card } from "react-bootstrap";
import {
  HandThumbsUp,
  ChatLeft,
  PencilFill,
  TrashFill,
} from "react-bootstrap-icons";
import Comment from "./Comment";
export default function Post({ post, user }) {
  const [commentShow, setCommentShow] = React.useState(false);
  const [deleteModalHidden, setDeleteModalHidden] = React.useState(true);

  const showComment = () => {
    setCommentShow(!commentShow);
  };

  const confirmDeleteHandler = () => setDeleteModalHidden(false);
  const closeDeleteHandler = () => setDeleteModalHidden(true);
  return (
    <>
      <Modal show={!deleteModalHidden} onHide={closeDeleteHandler}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Account</Modal.Title>
        </Modal.Header>
        <Modal.Body>Confirm to delete your account.</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeDeleteHandler}>
            Close
          </Button>
          <Button variant="danger" onClick={deleteHandler}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
      <Card>
        <Card.Header className="d-flex justify-content-between">
          {post.postBy}
          {post.postBy === user.data.email && (
            <div>
              <PencilFill></PencilFill>
              <TrashFill onClick={confirmDeleteHandler}></TrashFill>
            </div>
          )}
        </Card.Header>
        <Card.Body>
          {post.text}
          <hr></hr>
          <img src={post.img} className="img-fluid"></img>
        </Card.Body>
        <Card.Footer>
          <Container className="d-flex justify-content-around">
            <div>
              <HandThumbsUp />
              <span> Like</span>
            </div>
            <div onClick={showComment}>
              <ChatLeft />
              <span> Comment</span>
            </div>
          </Container>
        </Card.Footer>
      </Card>
      <Comment commentShow={commentShow} showComment={showComment}></Comment>
    </>
  );
}
