import * as React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Footer from "./UI/Footer";
import NavigationBar from "./UI/NavigationBar";
import SignUpForm from "./UI/SignUpForm";
import Container from "react-bootstrap/esm/Container";
import Hero from "./UI/Hero";
import { Outlet, Routes, Route } from "react-router-dom";

const userReducer = (state, action) => {
  switch (action.type) {
    case "SET_IS_JUST_SIGNED_UP":
      return {
        ...state,
        isJustSignedUp: action.payload,
      };
    case "SET_IS_EDITED":
      return {
        ...state,
        isEdited: action.payload,
      };
    case "SIGNUP_USER":
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
        isJustSignedUp: true,
      };
    case "SIGNIN_USER":
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
      };
    case "SIGNOUT_USER":
      return {
        ...state,
        data: null,
        isLoggedIn: false,
      };
    case "EDIT_USER":
      return {
        ...state,
        data: action.payload,
        isEdited: true,
      };
    case "DELETE_USER":
      return {
        ...state,
        data: null,
        isLoggedIn: false,
        isDeleted: false,
      };

    default:
      throw new Error();
  }
};

function App() {
  const [user, dispatchUser] = React.useReducer(userReducer, {
    data: JSON.parse(sessionStorage.getItem("user")),
    isLoggedIn: sessionStorage.getItem("user") !== null,
    isJustSignedUp: false,
    isDeleted: false,
    isEdited: false,
  });

  // a mock database holding informaiton on users
  const [users, setUsers] = React.useState(
    JSON.parse(localStorage.getItem("users")) ?? {}
  );

  // If user logs in, push user to sessionStorage.
  // If user logs out, pop user from sessionStorage.
  React.useEffect(() => {
    if (user?.isLoggedIn) {
      sessionStorage.setItem("user", JSON.stringify(user.data));
    } else {
      sessionStorage.removeItem("user");
    }
  }, [user.isLoggedIn]);

  // If user signs up
  React.useEffect(() => {
    if (user.isJustSignedUp) {
      // Update users
      users[user.data.email] = user.data;
      setUsers({ ...users });
      // Overwrite localStorage.users
      localStorage.setItem("users", JSON.stringify(users));
      dispatchUser({
        type: "SET_IS_JUST_SIGNED_UP",
        payload: false,
      });
    }
  }, [user.isJustSignedUp]);

  // If user deletes her/his profile
  React.useEffect(() => {
    if (user.isDeleted) {
      sessionStorage.removeItem("user");
      delete users[user.data.email];
    }
  }, [user.isDeleted]);

  // Whenever there is a change in user.data
  React.useEffect(() => {
    localStorage.setItem("user", JSON.stringify(user));
  }, [user.data]);

  // Whenever there is a change in users
  React.useEffect(() => {
    localStorage.setItem("users", JSON.stringify(users));
    // If user has edited her/his profile
    if (user.isEdited) {
      dispatchUser({
        type: "SET_IS_EDITED",
        payload: false,
      });
    }
  }, [users, user.isEdited]);

  return (
    <React.Fragment>
      <NavigationBar isLoggedIn={user.isLoggedIn} dispatchUser={dispatchUser} />
      <Outlet context={[user, dispatchUser, users]} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
