import { useState, useEffect } from "react";
import PostForm from "./PostForm";
import { Container, Row, Col } from "react-bootstrap";
import { useOutletContext } from "react-router-dom";
import Post from "./Post";
import Loading from "../Loading";

const numPosts = 5;

export default function Posts() {
  const [user, dispatchUser, users] = useOutletContext();
  const [isPosting, setIsPosting] = useState(true);
  // First read posts from localStorage if any. Otherwise, initiate posts with an empty array
  const [posts, setPosts] = useState(
    JSON.parse(localStorage.getItem("posts")) ?? []
  );

  const addPost = (post, isComment, parentPostId) => {
    setPosts([...posts, post]);
    dispatchUser({
      type: "ADD_POST",
      payload: posts.length,
    });
    if (isComment) {
      posts[parentPostId].children.push(post.postId);
      post.parentId = parentPostId;
    }
  };

  const removePost = (id) => {
    posts[id].isDeleted = true;
    setPosts([...posts]);
    dispatchUser({
      type: "DELETE_POST",
      payload: id,
    });
  };

  const editPost = (id, text) => {
    posts[id].text = text;
    setPosts([...posts]);
  };

  const isPostingHandler = (bool) => {
    setIsPosting(bool);
  };

  useEffect(() => {
    // TODO: write a more efficient code
    localStorage.setItem("posts", JSON.stringify(posts));
  }, [posts]);

  return (
    <Container className="my-5">
      <Row>
        <Col md={{ span: 8, offset: 2 }}>
          {isPosting ?? <p>"hello"</p>}
          <PostForm
            user={user}
            dispatchUser={dispatchUser}
            addPost={addPost}
            postId={posts.length}
            isComment={false}
            parentPostId={null}
            setIsPosting={isPostingHandler}
          ></PostForm>
          {posts
            .filter((post) => !post.isDeleted && post.parentId === null)
            .slice(-numPosts)
            .reverse()
            .map((post) => (
              <Post
                key={post.postId}
                user={user}
                dispatchUser={dispatchUser}
                post={post}
                posts={posts}
                childId={posts.length}
                addPost={addPost}
                removePost={removePost}
                editPost={editPost}
                users={users}
              ></Post>
            ))}
        </Col>
      </Row>
    </Container>
  );
}
