import * as React from "react";
import { Modal } from "react-bootstrap";
import Post from "./Post";
import PostForm from "./PostForm";

export default function Comment({
  post,
  posts,
  user,
  dispatchUser,
  addPost,
  editPost,
  removePost,
  commentId,
  commentShow,
  showComment,
}) {
  let isComment = true;

  return (
    <Modal show={commentShow} onHide={showComment} centered>
      <Modal.Header closeButton className="text-center">
        Comments
      </Modal.Header>
      <Modal.Body>
        {post.children
          .filter((postIdx) => !posts[postIdx].isDeleted)
          .map((postIdx) => (
            <Post
              key={postIdx}
              post={posts[postIdx]}
              posts={posts}
              childId={posts.length}
              user={user}
              dispatchUser={dispatchUser}
              addPost={addPost}
              removePost={removePost}
              editPost={editPost}
              isComment={isComment}
            ></Post>
          ))}
      </Modal.Body>
      <Modal.Footer>
        <PostForm
          user={user}
          dispatchUser={dispatchUser}
          addPost={addPost}
          postId={commentId}
          isComment={isComment}
          parentPostId={post.postId}
        />
      </Modal.Footer>
    </Modal>
  );
}
