import * as React from "react";
import { Modal } from "react-bootstrap";
import Post from "./Post";
import PostForm from "./PostForm";


export default function Comment({
  post,
  posts,
  user,
  dispatchUser,
  addPost,
  commentId,
  commentShow,
  showComment,
}) {
  let isComment = true;

  return (
    <Modal show={commentShow} onHide={showComment} centered>
      <Modal.Header closeButton className="text-center">
        Comments
      </Modal.Header>
      <Modal.Body>
        {post.children.map((postIdx) => (
          <Post key={postIdx} isComment={isComment}>{posts[postIdx].text}</Post>
        ))}
      </Modal.Body>
      <Modal.Footer>
        <PostForm
          user={user}
          dispatchUser={dispatchUser}
          addPost={addPost}
          postId={commentId}
          isComment={isComment}
          parentPostId={post.postId}
        />
      </Modal.Footer>
    </Modal>
  );
}
