import { Toast, ToastContainer } from "react-bootstrap";
import { ArrowReturnRight } from "react-bootstrap-icons";

export default function Comment({
  parentPost,
  posts,
  commentId,
  user,
  dispatchUser,
  addPost,
  removePost,
  editPost,
}) {
  return (
    <ToastContainer className="position-relative">
      <Toast>
        <Toast.Header closeButton={false} className="d-flex">
          <img
            src={user.data.avatarsrc}
            className="rounded me-2"
            width="30px"
            alt=""
          ></img>
          <strong className="me-auto">{user.data.email}</strong>
          <small>mins ago</small>
        </Toast.Header>
        <Toast.Body>
          {posts[commentId].text}
          <hr></hr>
          <a><ArrowReturnRight></ArrowReturnRight> Reply</a>
        </Toast.Body>
      </Toast>
    </ToastContainer>
  );
}
