import * as React from "react";
import { Form, Button, Container, Modal } from "react-bootstrap";
const axios = require("axios");

export default function MFA({ show, setShow, signUpAuthenticatedUser }) {
  const [qrcodeSrc, setQrcodeSrc] = React.useState("");
  const [secret, setSecret] = React.useState("");
  const [isOTPcorrect, setIsOTPcorrect] = React.useState(true);
  const url = "https://google-authenticator.p.rapidapi.com";
  const apiKey = "414bc5d180msh86e5db53aea8bbcp1bcd81jsn6915fb5401f0";

  async function getQrCode() {
    // options for the initial request to get a secret key
    let res;
    let options = {
      method: "GET",
      url: url + "/new_v2/",
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };
    res = await axios.request(options);
    let secret = res.data;
    setSecret(secret);

    // Re-define options again to make a request to get a QR code.
    options = {
      method: "GET",
      url: url + "/enroll/",
      params: {
        secret: secret,
        issuer: "lan",
        account: "JohnDoe",
      },
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };
    res = await axios.request(options);
    setQrcodeSrc(res.data);
  }

  React.useEffect(() => {
    getQrCode();
  }, []);

  const submitHandler = (e) => {
    e.preventDefault();
    const code = e.target[0].value;
    const options = {
      method: "GET",
      url: url + "/validate/",
      params: { code: code, secret: secret },
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };
    axios.request(options).then((response) => {
      console.log(response.data);
      if (response.data === "True") {
        signUpAuthenticatedUser(secret);
        setIsOTPcorrect(true);
        setShow(false);
      } else {
        isOTPcorrect(false);
      }
    });
  };

  return (
    <Modal show={show}>
      <Modal.Header closeButton>
        <Modal.Title className="text-center" >
          Scan the QR code from Google Authenticator
        </Modal.Title>
      </Modal.Header>
      <Modal.Body className="d-flex justify-content-center">
        <img src={qrcodeSrc} />
      </Modal.Body>
      <Modal.Footer>
        <Form onSubmit={submitHandler}>
          <Form.Group className="mb-3" controlId="authCode">
            <Form.Label>Authentication Code</Form.Label>
            <Form.Control
              type="text"
              required
              isInvalid={!isOTPcorrect}
            ></Form.Control>
            <Form.Control.Feedback type="invalid">
              The OTP is incorrect
            </Form.Control.Feedback>
          </Form.Group>
          <Button type="submit">Authenticate</Button>
        </Form>
      </Modal.Footer>
    </Modal>
  );
}
