function App() {
  const [currUser, setCurrUser] = React.useState({});

  // Store

  const addCurrUser = (user) => {
    setCurrUser({...user});
  };

  React.useEffect(() => {
    localStorage.setItem(
      JSON.stringify(currUser.email),
      JSON.stringify(currUser)
    );
  }, [currUser]);

  return (
    <React.Fragment>
      <NavigationBar />
      <Outlet context={[currUser, addCurrUser]} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
