import * as React from "react";
import { Modal } from "react-bootstrap";
import PostForm from "./PostForm";

export default function Comment({ commentShow, showComment }) {
  return (
    <Modal show={commentShow} onHide={showComment} centered>
      <Modal.Header closeButton className="text-center">
        Comments
      </Modal.Header>
      <Modal.Body></Modal.Body>
      <Modal.Footer>
        <PostForm />
      </Modal.Footer>
    </Modal>
  );
}
