      addUserToSS(action.payload);
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
      };
    case "SIGNOUT_USER":
      removeUserFromSS();
      return {
        ...state,
        data: null,
        isLoggedIn: false,
      };
    case "EDIT_USER":
      addUserToSS(action.payload);
      let res = {
        ...state,
        data: action.payload,
        users: { ...state.users, [action.payload.email]: action.payload },
      };
      updateLS(res.users);
      return res;
    case "DELETE_USER":
      removeUserFromSS();
      // First, remove the user from users
