import * as React from "react";
import { Toast, Form, Button, Modal } from "react-bootstrap";
import {
  ArrowReturnRight,
  PencilFill,
  TrashFill,
  ChatLeft,
} from "react-bootstrap-icons";
import convertTime from "../../convertTime";

export default function Comment({
  parentPost,
  posts,
  commentId,
  user,
  dispatchUser,
  addPost,
  removePost,
  editPost,
  replyHandler,
  expandComment,
  users,
}) {
  const [editHidden, setEditHidden] = React.useState(true);
  const [deleteModalHidden, setDeleteModalHidden] = React.useState(true);
  const [childrenToShow, setChildrenToShow] = React.useState([]);

  const editHandler = () => setEditHidden(false);

  const confirmDeleteHandler = () => setDeleteModalHidden(false);
  const closeDeleteHandler = () => setDeleteModalHidden(true);

  const replyClickHandler = () => {
    replyHandler(commentId, posts[commentId].postBy);
  };

  const deleteHandler = () => {
    removePost(commentId);
    setDeleteModalHidden(true);
  };

  const editSubmitHandler = (e) => {
    e.preventDefault();
    editPost(commentId, e.target[0].value);
    setEditHidden(true);
  };

  const showMoreCommentsHandler = () => {
    setChildrenToShow([...childrenToShow, ...posts[commentId].children]);
  };

  return (
    <>
      <Modal show={!deleteModalHidden} onHide={closeDeleteHandler}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Comment</Modal.Title>
        </Modal.Header>
        <Modal.Body>Confirm to delete your comment.</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeDeleteHandler}>
            Close
          </Button>
          <Button variant="danger" onClick={deleteHandler}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
      <Toast.Header closeButton={false} className="d-flex">
        <div>

        </div>
        <img
          src={users[posts[commentId].postBy].avatarsrc}
          className="rounded me-2"
          width="30px"
          alt=""
        ></img>
        <div>
          <strong className="me-auto">{`${user.data.firstName} ${user.data.lastName}`}</strong>
          <span>&nbsp; &#183; &nbsp;</span>
          <small>{convertTime(posts[commentId].datePosted)}</small>
        </div>

        <div>
          <a href="#">
            <PencilFill onClick={editHandler}></PencilFill>
          </a>
          <a href="#">
            <TrashFill onClick={confirmDeleteHandler}></TrashFill>
          </a>
        </div>
      </Toast.Header>
      <Toast.Body>
        {editHidden ? (
          posts[commentId].text
        ) : (
          <Form onSubmit={editSubmitHandler}>
            <Form.Group className="mb-3" controlId="formEditText">
              <Form.Label visuallyHidden="true">Text</Form.Label>
              <Form.Control
                as="textarea"
                required
                defaultValue={posts[commentId].text}
              ></Form.Control>
            </Form.Group>
            <Button variant="primary" type="submit">
              Edit
            </Button>
          </Form>
        )}

        <hr></hr>
        <div className="d-flex justify-content-between">
          <span onClick={replyClickHandler} role="button">
            <ArrowReturnRight></ArrowReturnRight> Reply
          </span>
          <span onClick={showMoreCommentsHandler} role="button">
            <ChatLeft />:<small>{posts[commentId].children.length}</small>
          </span>
        </div>
        {childrenToShow.map((postIdx) => (
          <Comment
            key={postIdx}
            parentPost={commentId}
            posts={posts}
            commentId={postIdx}
            user={user}
            dispatchUser={dispatchUser}
            addPost={addPost}
            removePost={removePost}
            editPost={editPost}
            replyHandler={replyHandler}
            users={users}
          ></Comment>
        ))}
      </Toast.Body>
    </>
  );
}
