import * as React from "react";
import { Modal, Toast, ToastContainer } from "react-bootstrap";
import Comment from "./Comment";
import PostForm from "./PostForm";

export default function Comments({
  parentPost,
  posts,
  user,
  dispatchUser,
  addPost,
  editPost,
  removePost,
  commentId,
  commentShow,
  showComment,
  users,
}) {
  const [parentId, setParentId] = React.useState(parentPost.postId);
  const [replyTo, setReplyTo] = React.useState(null);
  let isComment = true;

  const replyHandler = (parentId, replyTo) => {
    setParentId(parentId);
    setReplyTo(replyTo);
  };

  return (
    <Modal
      show={commentShow}
      onHide={showComment}
      centered
      className="modal-lg"
    >
      <Modal.Header closeButton className="text-center">
        Comments
      </Modal.Header>
      <Modal.Body className="position-relative">
        {parentPost.children.length === 0 ? (
          <p className="tex">No comments yet</p>
        ) : (
          parentPost.children
            .filter((postIdx) => !posts[postIdx].isDeleted)
            .map((postIdx) => (
              <ToastContainer key={postIdx}>
                <Toast>
                  <Comment
                    parentPost={parentPost}
                    posts={posts}
                    commentId={postIdx}
                    user={user}
                    dispatchUser={dispatchUser}
                    addPost={addPost}
                    removePost={removePost}
                    editPost={editPost}
                    replyHandler={replyHandler}
                    users={users}
                  ></Comment>
                </Toast>
              </ToastContainer>
            ))
        )}
      </Modal.Body>
      <Modal.Footer>
        <PostForm
          user={user}
          dispatchUser={dispatchUser}
          addPost={addPost}
          postId={commentId}
          isComment={isComment}
          parentPostId={parentId}
          replyTo={replyTo}
        />
      </Modal.Footer>
    </Modal>
  );
}
