import * as React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Footer from "./UI/Footer";
import NavigationBar from "./UI/NavigationBar";
import SignUpForm from "./UI/SignUpForm";
import Container from "react-bootstrap/esm/Container";
import Hero from "./UI/Hero";
import { Outlet, Routes, Route } from "react-router-dom";

// Add a user to localStorage.users
function addUserToLS(user) {
  let key = JSON.stringify(user.email);
  // Delete the property user.email in user so that no duplicate information is stored
  delete user.email;
  let value = JSON.stringify(user);

  // If a user is added for the first time
  if (localStorage.getItem("users") === null) {
    localStorage.setItem("users", `{${key}:${value}}`);
  } else {
    let users = localStorage.getItem("users");
    localStorage.setItem("users", `${users.slice(0, -1)}, ${key}:${value}}`);
  }
  // Add the property user.email back in so that it is accessible throughout the app
  user.email = JSON.parse(key);
}

const userReducer = (state, action) => {
  switch (action.type) {
    case "SIGNUP_USER":
      addUserToLS(action.payload);
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
      };
    case "SIGNIN_USER":
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
      };
  }
};

function App() {
  const [user, dispatchUser] = React.useReducer(userReducer, {
    data: null,
    isLoggedIn: false,
  });

  return (
    <React.Fragment>
      <NavigationBar />
      <Outlet context={[user, dispatchUser]} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
