import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import * as React from "react";

export default function SignUpForm() {
  const [isPasswordValid, setIsPasswordValid] = React.useState(false);
  const [isPasswordConfirmed, setIsPasswordConfirmed] = React.useState(false);

  const validatePassword = (event) => {
    // Only validate a second after the user stops typing
    // console.log(timeoutId);
    // clearTimeout(timeoutId);
    // let timeoutId = setTimeout()

    console.log(event.target.value);
    setIsPasswordValid(event.target.value.length >= 8 ? true : false);
    console.log(isPasswordValid);
  };

  return (
    <Form className="mt-5 mb-5">
      <header>
        <h1 className="text-center">Sign Up</h1>
      </header>
      <Form.Group className="mb-4" controlId="formSignupFirstName">
        <Form.Control
          type="text"
          required
          placeholder="First name"
          aria-label="First name"
        ></Form.Control>
      </Form.Group>
      <Form.Group className="mb-4" controlId="formSignupLastName">
        <Form.Control
          type="text"
          required
          placeholder="Last name"
          aria-label="Last name"
        ></Form.Control>
      </Form.Group>
      <Form.Group className="mb-5" controlId="formSignupEmail">
        <Form.Control
          type="email"
          required
          placeholder="Email"
          aria-label="Email"
        />
      </Form.Group>

      <Form.Group className="mb-4" controlId="formSignupPassword">
        <Form.Control
          type="password"
          required
          placeholder="Password"
          aria-label="Password"
          onChange={validatePassword}
        />
        <Form.Control.Feedback type="invalid"></Form.Control.Feedback>
      </Form.Group>
      <Form.Group className="mb-2" controlId="formSignupPasswordConfirm">
        <Form.Control
          type="password"
          required
          placeholder="Confirm"
          aria-label="Confirm"
        />
        <Form.Text className="text-muted">
          Use 8 or more character with a mix of letters, numbers & symbols
        </Form.Text>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formSignupShowPassword">
        <Form.Check type="checkbox" label="Show password" />
      </Form.Group>
      <Button variant="primary" type="submit">
        Sign Up
      </Button>
    </Form>
  );
}
