import * as React from "react";
import { Modal, Container, Toast, Button, Form } from "react-bootstrap";
import {
  HandThumbsUp,
  ChatLeft,
  PencilFill,
  TrashFill,
} from "react-bootstrap-icons";
import Comments from "./Comments";
export default function Post({
  post,
  posts,
  childId,
  user,
  dispatchUser,
  addPost,
  removePost,
  editPost,
  users,
}) {
  const [commentShow, setCommentShow] = React.useState(false);
  const [deleteModalHidden, setDeleteModalHidden] = React.useState(true);
  const [editHidden, setEditHidden] = React.useState(true);

  const showComment = () => {
    setCommentShow(!commentShow);
  };

  const confirmDeleteHandler = () => setDeleteModalHidden(false);
  const closeDeleteHandler = () => setDeleteModalHidden(true);

  const deleteHandler = () => {
    removePost(post.postId);
    setDeleteModalHidden(true);
  };

  const editHandler = () => setEditHidden(false);

  const editSubmitHandler = (e) => {
    e.preventDefault();
    editPost(post.postId, e.target[0].value);
    setEditHidden(true);
  };

  return (
    <>
      <Modal show={!deleteModalHidden} onHide={closeDeleteHandler}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>Confirm to delete your post.</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeDeleteHandler}>
            Close
          </Button>
          <Button variant="danger" onClick={deleteHandler}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
      <ToastContainer className="position-relative ml-2"></ToastContainer>
      <Toast>
        <Toast.Header className="d-flex justify-content-between">
          {post.postBy}
          {post.postBy === user.data.email && (
            <div>
              <PencilFill onClick={editHandler}></PencilFill>
              <TrashFill onClick={confirmDeleteHandler}></TrashFill>
            </div>
          )}
        </Toast.Header>
        <Toast.Body>
          {editHidden ? (
            post.text
          ) : (
            <Form onSubmit={editSubmitHandler}>
              <Form.Group className="mb-3" controlId="formEditText">
                <Form.Label visuallyHidden="true">First name</Form.Label>
                <Form.Control
                  as="textarea"
                  required
                  defaultValue={post.text}
                ></Form.Control>
              </Form.Group>
              <Button variant="primary" type="submit">
                Edit
              </Button>
            </Form>
          )}

          <hr></hr>
          <img src={post.img} className="img-fluid"></img>
        </Toast.Body>
      </Toast>

      {/* <Card.Footer>
          <Container className="d-flex justify-content-around">
            <div>
              <HandThumbsUp />
              <span> Like</span>
            </div>
            <div onClick={showComment}>
              <ChatLeft />
              <span> Comment</span>
            </div>
          </Container>
        </Card.Footer> */}

      <Comments
        user={user}
        parentPost={post}
        posts={posts}
        dispatchUser={dispatchUser}
        addPost={addPost}
        commentId={childId}
        removePost={removePost}
        editPost={editPost}
        commentShow={commentShow}
        showComment={showComment}
        users={users}
      ></Comments>
    </>
  );
}
