  } else {
    let users = localStorage.getItem("users");
    localStorage.setItem("users", `${users.slice(0, -1)}, ${userJson}]`);
  }
}

const userReducer = (state, action) => {
  switch (action.type) {
    case 'SIGNUP_USER':
      addUserToLS(action.payload)
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
      };
    case 'SIGNIN_USER':
      return {
        ...state,
        data: action.payload,
        isLoggedIn: true,
      };
  }
}

function App() {
  const [user, dispatchUser] = React.useReducer(
    userReducer, {data: null, isLoggedIn: false}
  )

  return (
    <React.Fragment>
      <NavigationBar />
      <Outlet context={[user, dispatchUser]} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
