export default function getTotalNumChildPosts(post) {
  // Recursively find out the total number of child posts
  // Base case: there is no element in the children property
  let res;
  if (post.children.length === 0) {
    res = 0;
  } else {
    res = 0;
    for (let i = 0; i < post.children.length; i++) {
      if (!posts[post.children[i]].isDeleted) {
        console.log("not deleted child post:");
        console.log(posts[post.children[i]]);
        res = res + 1 + getTotalNumChildPosts(posts[post.children[i]]);
      }
    }
  }

  return res;
}
