import * as React from "react";
import {
  Card,
  Container,
  Form,
  Tabs,
  Tab,
  Button,
  Modal,
} from "react-bootstrap";
import { useOutletContext } from "react-router-dom";
import { PencilFill, TrashFill } from "react-bootstrap-icons";
import PasswordInput from "./PasswordInput";

export default function Profile() {
  const [user, dispatchUser] = useOutletContext();
  const [editHidden, setEditHidden] = React.useState(true);
  const [deleteModalHidden, setDeleteModalHidden] = React.useState(true);

  const editShowHandler = (event) => {
    setEditHidden(!editHidden);
  };

  const editNameHandler = (event) => {
    dispatchUser({
      type: "EDIT_USER",

      payload: {
        ...user.data,
        firstName: event.target[0].value,
        lastName: event.target[1].value,
      },
    });
    setEditHidden(!editHidden);
  };

  const confirmDeleteHandler = () => setDeleteModalHidden(false);
  const closeDeleteHandler = () => setDeleteModalHidden(true)

  return (
    <Container>
      <Modal show={!deleteModalHidden}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Account</Modal.Title>
        </Modal.Header>
        <Modal.Body>Confirm to delete your account.</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary">Close</Button>
          <Button variant="danger">Delete</Button>
        </Modal.Footer>
      </Modal>
      <Card>
        <Card.Header className="d-flex justify-content-between align-items-center">
          <h1>Profile</h1>
          <span>
            <a href="#" onClick={editShowHandler}>
              <PencilFill />
            </a>
            <a href="#" onClick={confirmDeleteHandler}>
              <TrashFill />
            </a>
          </span>
        </Card.Header>
        <Card.Img
          variant="top"
          src={user.data.avatarsrc}
          hidden={!editHidden}
        />
        <Card.Body>
          <Card.Title>
            <h2 hidden={!editHidden}>
              {user.data.firstName + " " + user.data.lastName}
            </h2>
            <Tabs
              className="my-5"
              defaultActiveKey="name"
              justify
              hidden={editHidden}
            >
              {/* <Tab eventKey="avatar" title="Avatar"></Tab> */}
              <Tab eventKey="name" title="Name">
                <Form
                  className="my-5"
                  hidden={editHidden}
                  onSubmit={editNameHandler}
                >
                  <Form.Group className="mb-3" controlId="formEditFirstName">
                    <Form.Label visuallyHidden="true">First name</Form.Label>
                    <Form.Control
                      type="text"
                      required
                      defaultValue={user.data.firstName}
                    ></Form.Control>
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formEditLastName">
                    <Form.Label visuallyHidden="true">Last name</Form.Label>
                    <Form.Control
                      type="text"
                      required
                      defaultValue={user.data.lastName}
                    ></Form.Control>
                  </Form.Group>
                  <Container>
                    <div className="d-grid">
                      <Button variant="primary" type="submit">
                        Edit
                      </Button>
                    </div>
                  </Container>
                </Form>
              </Tab>
              {/* <Tab eventKey="password" title="Password">
                <PasswordInput></PasswordInput>
              </Tab> */}
            </Tabs>
          </Card.Title>

          <hr />
          <Card.Subtitle className="mb-2 text-muted">
            {user.data.email}
          </Card.Subtitle>
          <hr />
          <Card.Text>Joined: {user.data.joinedDate}</Card.Text>
        </Card.Body>
      </Card>
    </Container>
  );
}
