    setEditHidden(true);
  };

  return (
    <ToastContainer className="position-relative">
      <Toast>
        <Toast.Header closeButton={false} className="d-flex">
          <img
            src={user.data.avatarsrc}
            className="rounded me-2"
            width="30px"
            alt=""
          ></img>
          <strong className="me-auto">{user.data.email}</strong>
          <div>
            <PencilFill></PencilFill>
            <TrashFill></TrashFill>
          </div>
        </Toast.Header>
        <Toast.Body>
        {editHidden ? (
            posts[commentId].text
          ) : (
            <Form onSubmit={editSubmitHandler}>
              <Form.Group className="mb-3" controlId="formEditText">
                <Form.Label visuallyHidden="true">First name</Form.Label>
                <Form.Control
                  as="textarea"
                  required
                  defaultValue={post.text}
                ></Form.Control>
              </Form.Group>
              <Button variant="primary" type="submit">
                Edit
              </Button>
            </Form>
          )}

          <hr></hr>
          <div className="d-flex justify-content-between">
            <a className="text-decoration-none" href="#" onClick={replyHandler}>
              <ArrowReturnRight></ArrowReturnRight> Reply
            </a>
            <small>mins ago</small>
          </div>
        </Toast.Body>
      </Toast>
    </ToastContainer>
  );
}
