import * as React from "react";
import PostForm from "./PostForm";
import { Container } from "react-bootstrap";
import { useOutletContext } from "react-router-dom";
import PostContainer from "./PostContainer";

const numPosts = 5;

export default function Posts() {
  const [user, dispatchUser] = useOutletContext();
  // First read posts from localStorage if any. Otherwise, initiate posts with an empty array
  const [posts, setPosts] = React.useState(
    JSON.parse(localStorage.getItem("posts")) ?? []
  );

  const addPost = (item) => {
    setPosts([...posts, item]);

  };

  React.useEffect(() => {
    // TODO: write a more efficient code
    localStorage.setItem("posts", JSON.stringify(posts));
  }, [posts]);

  return (
    <Container className="my-5">
      <PostForm
        user={user}
        dispatchUser={dispatchUser}
        addPost={addPost}
        postId={posts.length}
      ></PostForm>
      {posts
        .slice(-numPosts)
        .reverse()
        .map((post) => (
          <PostContainer key={post.postId} post={post}></PostContainer>
        ))}
    </Container>
  );
}
