import * as React from "react";
import { Form, Button, Container, Modal } from "react-bootstrap";
const axios = require("axios");

export default function MFA({ show, setShow, onSuccess, hideQRcode, user }) {
  const [qrcodeSrc, setQrcodeSrc] = React.useState("");
  const [secret, setSecret] = React.useState("");
  const [isOTPcorrect, setIsOTPcorrect] = React.useState(true);
  const url = "https://google-authenticator.p.rapidapi.com";
  const apiKey = "414bc5d180msh86e5db53aea8bbcp1bcd81jsn6915fb5401f0";

  async function getQrCode() {
    // options for the initial request to get a secret key
    let res;
    let options = {
      method: "GET",
      url: url + "/new_v2/",
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };
    res = await axios.request(options);
    let secret = res.data;
    setSecret(secret);

    // Re-define options again to make a request to get a QR code.
    options = {
      method: "GET",
      url: url + "/enroll/",
      params: {
        secret: secret,
        issuer: "lan",
        account: "JohnDoe",
      },
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };
    res = await axios.request(options);
    setQrcodeSrc(res.data);
  }

  React.useEffect(() => {
    if (!hideQRcode) {
      getQrCode();
    }
  }, []);

  const submitHandler = (e) => {
    e.preventDefault();
    const code = e.target[0].value;
    const options = {
      method: "GET",
      url: url + "/validate/",
      params: { code: code, secret: user ? user.secret : secret },
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };
    axios.request(options).then((response) => {
      console.log(response.data);
      if (response.data === "True") {
        if (hideQRcode) {
          onSuccess();
        } else {
          onSuccess(secret);
        }

        setIsOTPcorrect(true);
        setShow(false);
      } else {
        setIsOTPcorrect(false);
      }
    });
  };

  const handleClose = () => {
    setShow(false);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title className="text-center" hidden={hideQRcode}>
          Scan the QR code in \&nbsp Google Authenticator
        </Modal.Title>
      </Modal.Header>
      <Modal.Body className="d-flex justify-content-center" hidden={hideQRcode}>
        <img src={qrcodeSrc} />
      </Modal.Body>
      <Modal.Footer className="d-flex justify-content-center">
        <Form onSubmit={submitHandler}>
          <Form.Group className="mb-3 text-center" controlId="authCode">
            <Form.Label>Authentication Code</Form.Label>
            <Form.Control
              type="text"
              required
              isInvalid={!isOTPcorrect}
            ></Form.Control>
            <Form.Control.Feedback type="invalid">
              The OTP is incorrect
            </Form.Control.Feedback>
          </Form.Group>
          <div className="text-center">
            <Button type="submit">Authenticate</Button>
          </div>
        </Form>
      </Modal.Footer>
    </Modal>
  );
}
