import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import * as React from "react";
import Container from "react-bootstrap/esm/Container";
import { useOutletContext, useNavigate } from "react-router-dom";
import unlock from "./unlock.svg";

const Time = {
  Day: 0,
  Month: 1,
  Date: 2,
  Year: 3,
};

function formatDate() {
  let date = new Date().toString().split(" ");
  return `${date[Time.Day]} ${date[Time.Date]} ${date[Time.Month]} ${
    date[Time.Year]
  }`;
}

export default function SignUpForm() {
  const [isPasswordInvalid, setIsPasswordInvalid] = React.useState(false);
  const [isPasswordIdentical, setIsPasswordIdentical] = React.useState(true);
  const [isPasswordVisible, setIsPasswordVisble] = React.useState(false);
  const [isEmailValid, setIsEmailValid] = React.useState(true);

  const [user, dispatchUser] = useOutletContext();
  const navigate = useNavigate();

  let validatePasswordTimeoutId;
  let confirmPasswordTimeoutId;
  let validateEmailTimeoutId;
  const timeDelayed = 1000;

  const validateEmail = (event) => {
    clearTimeout(validateEmailTimeoutId);
    validateEmailTimeoutId = setTimeout(() => {
      if (localStorage.getItem("users")?.includes(`"${event.target.value}":`)) {
        setIsEmailValid(false);
      } else {
        setIsEmailValid(true);
      }
    }, timeDelayed);
  };

  const validatePassword = (event) => {
    // Only validate a second after the user stops typing
    clearTimeout(validatePasswordTimeoutId);
    validatePasswordTimeoutId = setTimeout(() => {
      /* A regex pattern that matches invalid passwords
         - Anything with less than eight characters OR
         - Anything with no numbers OR
         - Anything with no uppercase letter OR
         - Anything with no lowercase letter OR
         - Anything with no special characters
      */
      const regex = "^(.{0,7}|[^0-9]*|[^A-Z]*|[^a-z]*|[a-zA-Z0-9]*)$";
      console.log(event.target.value.match(regex));

      // If there are any matches, the password is invalid
      setIsPasswordInvalid(event.target.value.match(regex) ? true : false);
    }, timeDelayed);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Add a user to LocalStorage
    console.log(event.target);
    // localStorage.curruser = {
    let user = {
      firstName: event.target[0].value,
      lastName: event.target[1].value,
      email: event.target[2].value,
      password: event.target[3].value,
      joinedDate: formatDate(),
    };
    dispatchUser({
      type: "SIGNUP_USER",
      payload: user,
    });

    setTimeout(() => {
      navigate("/profile");
    }, timeDelayed);

    // }
  };

  const confirmPassword = (event) => {
    confirmPasswordTimeoutId = setTimeout(() => {
      setIsPasswordIdentical(
        event.target.value ===
          document.querySelector("#formSignupPassword").value
          ? true
          : false
      );
    }, timeDelayed);
  };

  const togglePasswordVisability = (event) => {
    setIsPasswordVisble(!isPasswordVisible);
  };

  const unlockImage = (
    <div className="d-flex flex-column align-items-center">
      <object data={unlock} type="image/svg+xml"></object>
    </div>
  );

  const getFormJSX = () => {
    return (
      <Container>
        <Form className="my-5" onSubmit={handleSubmit}>
          <header className="mb-5">
            <h1 className="text-center">Sign up</h1>
          </header>
          <Form.Group className="mb-4" controlId="formSignupFirstName">
            <Form.Label visuallyHidden="true">First name</Form.Label>
            <Form.Control
              type="text"
              required
              placeholder="First name"
              defaultValue="Myeonghoon"
            ></Form.Control>
          </Form.Group>
          <Form.Group className="mb-4" controlId="formSignupLastName">
            <Form.Label visuallyHidden="true">Last name</Form.Label>
            <Form.Control
              type="text"
              required
              placeholder="Last name"
              defaultValue="Sun"
            ></Form.Control>
          </Form.Group>
          <Form.Group className="mb-5" controlId="formSignupEmail">
            <Form.Label visuallyHidden>Email</Form.Label>
            <Form.Control
              type="email"
              required
              placeholder="Email"
              defaultValue="MyeonghoonSun@lan.com.au"
              onChange={validateEmail}
              isInvalid={!isEmailValid}
            />
            <Form.Control.Feedback type="invalid">
              The email already exists
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group className="mb-4" controlId="formSignupPassword">
            <Form.Label visuallyHidden>Password</Form.Label>
            <Form.Control
              type={isPasswordVisible ? "text" : "password"}
              required
              placeholder="Password"
              onChange={validatePassword}
              isInvalid={!!isPasswordInvalid}
              defaultValue="abcDEF1!"
            />
            <Form.Control.Feedback type="invalid">
              Use 8 or more character with a mix of lowercase and uppercase
              letters, numbers & symbols
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group className="mb-2" controlId="formSignupPasswordConfirm">
            <Form.Label visuallyHidden="true">Confirm Password</Form.Label>
            <Form.Control
              type={isPasswordVisible ? "text" : "password"}
              required
              placeholder="Confirm"
              isInvalid={!isPasswordIdentical}
              onChange={confirmPassword}
            />
            <Form.Control.Feedback type="invalid">
              Passwords are not identical
            </Form.Control.Feedback>
            <Form.Text className="text-muted">
              Use 8 or more character with a mix of lowercase and uppercase
              letters, numbers & symbols
            </Form.Text>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formSignupShowPassword">
            <Form.Check
              type="checkbox"
              label="Show password"
              onClick={togglePasswordVisability}
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Sign Up
          </Button>
        </Form>
      </Container>
    );
  };

  return user.data === null ? getFormJSX() : unlockImage;
}
