import * as React from "react";
import {
  Row,
  Col,
  Card,
  Form,
  Button,
  Container,
  ProgressBar,
  OverlayTrigger,
  Tooltip,
} from "react-bootstrap";
import { Image } from "react-bootstrap-icons";

const api_key = "213667524527924";
const cloud_name = "duc4zmhl7";

export default function PostForm({ user, dispatchUser }) {
  const [isPostable, setIsPostable] = React.useState(false);
  const [numCharsTyped, setNumCharsTyped] = React.useState(0);
  const [progressBarVariant, setProgressBarVariant] = React.useState("success");
  const wordLimit = 250;

  React.useEffect(() => {
    // Disable/enable the post button
    clearTimeout(validateTextareaTimeoutId);
    validateTextareaTimeoutId = setTimeout(() => {
      setIsPostable(validateTextarea(numCharsTyped));
    }, 500);

    if (Math.floor((numCharsTyped / wordLimit) * 100) <= 70) {
      setProgressBarVariant("success");
    } else if (numCharsTyped <= wordLimit) {
      setProgressBarVariant("warning");
    } else {
      setProgressBarVariant("danger");
    }
  }, [numCharsTyped]);

  let validateTextareaTimeoutId;

  const validateTextarea = (num) => num > 0 && num <= wordLimit;

  const textareaChangeHandler = (event) => {
    setNumCharsTyped(event.target.value.length);
  };

  const imageUploadHandler = (event) => {
    let img = document.querySelector("#myImg");
    img.src = URL.createObjectURL(event.target.files[0]);
  };

  const postHandler = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append("file", document.querySelector("#imageUpload").files[0]);
    data.append("api_key", api_key);
    data.append("signature", "HxL41grKT1esiBNlzglNFuj2Pkc");
    data.append("timestamp", new Date().getTime());

    fetch('https://api.cloudinary.com/v1_1/', {

    })
  };

  return (
    <Card>
      <Row className="gx-0">
        <Col xs={2}>
          <Card.Img src={user.data.avatarsrc}></Card.Img>
        </Col>
        <Col>
          <Form.Control
            as="textarea"
            rows={3}
            onChange={textareaChangeHandler}
          ></Form.Control>
          <img id="myImg" src="#" className="img-fluid"></img>

          <ProgressBar
            className="my-3"
            variant={progressBarVariant}
            now={Math.floor((numCharsTyped / wordLimit) * 100)}
            label={
              progressBarVariant !== "success" &&
              `${wordLimit - numCharsTyped} letters left`
            }
          ></ProgressBar>

          <Container className="d-flex justify-content-between mb-3">
            <Form onSubmit={postHandler}>
              <Form.Group controlId="imageUpload">
                <Form.Label>
                  <Image></Image>
                </Form.Label>
                <Form.Control
                  type="file"
                  className="d-none"
                  onChange={imageUploadHandler}
                ></Form.Control>
              </Form.Group>

              <Button type="submit" disabled={!isPostable}>
                Post
              </Button>
            </Form>
          </Container>
        </Col>
      </Row>
    </Card>
  );
}
