import * as React from "react";
import { Container, Button, Form } from "react-bootstrap";
import { useOutletContext, useNavigate } from "react-router-dom";
import unlock from "./unlock.svg";
import Loading from "./Loading";
import PasswordInput from "./PasswordInput";

const Time = {
  Day: 0,
  Month: 1,
  Date: 2,
  Year: 3,
};

function formatDate() {
  let date = new Date().toString().split(" ");
  return `${date[Time.Day]} ${date[Time.Date]} ${date[Time.Month]} ${
    date[Time.Year]
  }`;
}

export default function SignUpForm() {
  const [isEmailValid, setIsEmailValid] = React.useState(true);
  const [isLoading, setIsLoading] = React.useState(false);
  const [canSubmit, setCanSubmit] = React.useState(false);

  const [user, dispatchUser] = useOutletContext();
  const navigate = useNavigate();

  let validateEmailTimeoutId;
  const timeDelayed = 1000;

  function validateEmail(email) {
    return !(email in user.users);
  }

  const validateEmailHandler = (event) => {
    clearTimeout(validateEmailTimeoutId);
    validateEmailTimeoutId = setTimeout(() => {
      setIsEmailValid(validateEmail(event.target.value));
    }, timeDelayed);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    let email = event.target[2].value;
    let password = event.target[3].value;
    let confirmedPassword = event.target[4].value;
      let user = {
        firstName: event.target[0].value,
        lastName: event.target[1].value,
        email: email,
        password: confirmedPassword,
        joinedDate: formatDate(),
      };
      dispatchUser({
        type: "SIGNUP_USER",
        payload: user,
      });

      setIsLoading(true);

      setTimeout(() => {
        setIsLoading(false);
        navigate("/profile");
      }, timeDelayed);
  };

  return (
    <Container>
      <Loading show={isLoading}>Logging in</Loading>
      <Form className="my-5" onSubmit={submitHandler}>
        <header className="mb-5">
          <h1 className="text-center">Sign up</h1>
        </header>
        <Form.Group className="mb-4" controlId="formSignupFirstName">
          <Form.Label visuallyHidden="true">First name</Form.Label>
          <Form.Control
            type="text"
            required
            placeholder="First name"
            defaultValue="Myeonghoon"
          ></Form.Control>
        </Form.Group>
        <Form.Group className="mb-4" controlId="formSignupLastName">
          <Form.Label visuallyHidden="true">Last name</Form.Label>
          <Form.Control
            type="text"
            required
            placeholder="Last name"
            defaultValue="Sun"
          ></Form.Control>
        </Form.Group>
        <Form.Group className="mb-5" controlId="formSignupEmail">
          <Form.Label visuallyHidden>Email</Form.Label>
          <Form.Control
            type="email"
            required
            placeholder="Email"
            defaultValue="MyeonghoonSun@lan.com.au"
            onChange={validateEmailHandler}
            isInvalid={!isEmailValid}
          />
          <Form.Control.Feedback type="invalid">
            The email already exists
          </Form.Control.Feedback>
        </Form.Group>

        <PasswordInput timeDelayed={timeDelayed}/>

        <Button variant="primary" type="submit" disabled={canSubmit}>
          Sign Up
        </Button>
      </Form>
    </Container>
  );
}
