import { Row, Col, Card, Form } from "react-bootstrap";

export default function PostForm({ user, dispatchUser }) {
  return (
    <Card>
      <Row>
        <Col xs={2} className="mt-2">
          <Card.Img src={user.data.avatarsrc}></Card.Img>
        </Col>
        <Col className="px-0">
          <Form.Control
            as="textarea"
            className="px-0 py-0 mt-2"
            rows={5}
          ></Form.Control>
        </Col>
      </Row>
    </Card>
  );
}
