function App() {
  const [currUser, setCurrUser] = React.useState(null);

  const addUser = (user) => {
    setCurrUser(user);

    // If localStorage does not have users as a property
    if (typeof localStorage.users === "undefined") {
      localStorage.setItem("users", []);
    }
    localStorage.getItem("users").push(user);
    console.log("The curr user has been pushed");
  };

  return (
    <React.Fragment>
      <NavigationBar />
      <Outlet context={addUser} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
