import * as React from "react";
import { Modal, Container, Card } from "react-bootstrap";
import {
  HandThumbsUp,
  ChatLeft,
  PencilFill,
  TrashFill,
} from "react-bootstrap-icons";
import Comment from "./Comment";
export default function Post({ post }) {
  const [commentShow, setCommentShow] = React.useState(false);
  const showComment = () => {
    setCommentShow(!commentShow);
  };

  return (
    <>
      <Card>
        <Card.Header className="d-flex">
          {post.postBy}
          <div>
            <PencilFill></PencilFill>
            <TrashFill></TrashFill>
          </div>
        </Card.Header>
        <Card.Body>
          {post.text}
          <hr></hr>
          <img src={post.img} className="img-fluid"></img>
        </Card.Body>
        <Card.Footer>
          <Container className="d-flex justify-content-around">
            <div>
              <HandThumbsUp />
              <span> Like</span>
            </div>
            <div onClick={showComment}>
              <ChatLeft />
              <span> Comment</span>
            </div>
          </Container>
        </Card.Footer>
      </Card>
      <Comment commentShow={commentShow} showComment={showComment}></Comment>
    </>
  );
}
