// Add a user to localStorage.users
function addUserToLS(user) {
  // If a user is added for the first time
  let userJson = JSON.stringify(user);
  if (localStorage.getItem("users") === null) {
    localStorage.setItem("users", `[${userJson}]`);
  } else {
    let users = localStorage.getItem("users");
    localStorage.setItem("users", `${users.slice(0, -1)}, ${userJson}]`);
  }
}

function App() {
  const [currUser, setCurrUser] = React.useState({});``

  // Store

  const addCurrUser = (user) => {
    setCurrUser({ ...user });
    addUserToLS(user);
  };

  return (
    <React.Fragment>
      <NavigationBar />
      <Outlet context={[currUser, addCurrUser]} />

      <Footer />
    </React.Fragment>
  );
}

export default App;
