import * as React from "react";
import { Form, Button } from "react-bootstrap";
const axios = require("axios");

export default function SignUpMFA() {
  const [qrcodeSrc, setQrcodeSrc] = React.useState("");
  const [secret, setSecret] = React.useState("");
  const url = "https://google-authenticator.p.rapidapi.com";
  const apiKey = "414bc5d180msh86e5db53aea8bbcp1bcd81jsn6915fb5401f0";

  async function getQrCode() {
    // options for the initial request to get a secret key
    let options = {
      method: "GET",
      url: url + "/new_v2/",
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };
    let secret = await axios.request(options).data;
    setSecret(secret);

    // Re-define options again to make a request to get a QR code.
    options = {
      method: "GET",
      url: url + "/enroll/",
      params: {
        secret: secret,
        issuer: "lan",
        account: "JohnDoe",
      },
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };
    setQrcodeSrc(await axios.request(options).data);
  }

  React.useEffect(() => {
    getQrCode();
  }, []);

  const submitHandler = (e) => {
    e.preventDefault();
    const code = e.target[0].value;
    const options = {
      method: "GET",
      url: url + "/validate/",
      params: { code: code, secret: secret },
      headers: {
        "X-RapidAPI-Key": apiKey,
        "X-RapidAPI-Host": "google-authenticator.p.rapidapi.com",
      },
    };

  };

  return (
    <>
      <h1>Scan the barcode from Google Authenticator</h1>;
      <img src={qrcodeSrc} />
      <Form onSubmit={submitHandler}>
        <Form.Group controlId="authCode">
          <Form.Label>Authentication Code</Form.Label>
          <Form.Control type="text" required></Form.Control>
        </Form.Group>
        <Button type="submit">Authenticate</Button>
      </Form>
    </>
  );
}
