import * as React from "react";
import { Modal, Toast, ToastContainer } from "react-bootstrap";
import Comment from "./Comment";
import PostForm from "./PostForm";
import styles from "./Comments.module.css";

export default function Comments({
  parentPost,
  posts,
  user,
  dispatchUser,
  addPost,
  editPost,
  removePost,
  commentId,
  commentShow,
  showComment,
  users,
}) {
  const [parentId, setParentId] = React.useState(parentPost.postId);
  const [replyTo, setReplyTo] = React.useState(null);
  let isComment = true;

  const replyHandler = (parentId, replyTo) => {
    setParentId(parentId);
    setReplyTo(replyTo);
  };

  return (
    <Modal show={commentShow} onHide={showComment} centered fullscreen>
      <Modal.Header closeButton className="text-center">
        Comments
      </Modal.Header>
      <Modal.Body className="position-relative">
        {parentPost.children.length === 0 ? (
          <p className="tex">No comments yet</p>
        ) : (
          <ToastContainer key={postIdx} position="top-center">
          {parentPost.children
            .filter((postIdx) => !posts[postIdx].isDeleted)
            .map((postIdx) => (

                <Toast className={styles.toast}>
                  <Comment
                    parentPost={parentPost}
                    posts={posts}
                    commentId={postIdx}
                    user={user}
                    dispatchUser={dispatchUser}
                    addPost={addPost}
                    removePost={removePost}
                    editPost={editPost}
                    replyHandler={replyHandler}
                    users={users}
                  ></Comment>
                </Toast>

            ))}
            </ToastContainer>
        )}
      </Modal.Body>
      <Modal.Footer className="d-flex justify-content-center">
        <PostForm
          user={user}
          dispatchUser={dispatchUser}
          addPost={addPost}
          postId={commentId}
          isComment={isComment}
          parentPostId={parentId}
          replyTo={replyTo}
        />
      </Modal.Footer>
    </Modal>
  );
}
