import { Row, Col } from "react-bootstrap";
import Container from "react-bootstrap/esm/Container";
import styles from "./Hero.module.css";
import heroImage from "./hero.jpg";

export default function Hero() {
  return (
    <Container className={`jumbotron my-5 ${styles.hero}`}>
      <h1 className="display-4">
        Social Media Platform
        <br />
        for Loop Agile
      </h1>
      <p className="lead">Built to enable communications within Loop Agile.</p>
      <hr></hr>
      <Row>
        <Col className="col-lg-6"></Col>
        <Col className="col-12 col-lg-6 d-flex justify-content-lg-end justify-content-end">
          <img className={`img-fluid ${styles.heroImg}`} src={heroImage}></img>
        </Col>
      </Row>
    </Container>
  );
}
